const jwt=require("jsonwebtoken");var User=require("../models/userModel");exports.login=function(e,n,s){User.find({email:e.body.email},function(s,o){if(console.log(o),s)return n.status(400).send("Usuário inválido!");if(o.length>0&&o[0].pwd===e.body.pwd){var r=o[0]._id,i=jwt.sign({userId:r},process.env.SECRET,{expiresIn:10800});return n.json({auth:!0,token:i,data:{name:o[0].name}})}n.status(400).json({message:"Login inválido!"})})},exports.register=function(e,n){new User({email:e.body.email,name:e.body.name,pwd:e.body.pwd,username:e.body.username}).save(function(e){if(e)return n.status(400).send("Não foi possível registrar! Tente novamente mais tarde.");n.send("Registro bem sucedido.")})};