var Smartphone=require("../models/smartphonesModel");exports.test=function(e,o){o.send("Olá! Teste ao Controller")},exports.create=function(e,o){new Smartphone({nome:e.body.nome,marca:e.body.marca}).save(function(e){if(e)return o.status(501).send("Registro de Smartphone não foi criado");o.send("Registro de Smartphone criado com sucesso")})},exports.details=function(e,o){Smartphone.find({marca:e.params.id},function(e,n){if(e)return o.status(501).send("Registro de Smartphone não encontrado");o.send(n)})};