const mongoose=require("mongoose");var encrypt=require("mongoose-encryption");const Schema=mongoose.Schema;let UserSchema=new Schema({email:{type:String,required:!0,unique:!0,max:100},username:{type:String,required:!0,unique:!0,max:20},name:{type:String,required:!0,max:100},pwd:{type:String,required:!0},favorites:{type:Array,required:!0,default:[]}},{timestamps:!0});var encKey=process.env.ENCKEY,sigKey=process.env.SIGKEY;UserSchema.plugin(encrypt,{encryptionKey:encKey,signingKey:sigKey,encryptedFields:["pwd"]}),module.exports=mongoose.model("User",UserSchema);